import React, { useState, useEffect } from 'react'
import axios from 'axios'
import Header from './components/ui/Header'
import CharacterGrid from './components/characters/CharacterGrid'
import Filter from './components/ui/Filter'
import Pagination from './components/ui/Pagination'
import './App.css';

const App = () => {

  const [items, setItems] = useState([]); 
  const [isLoading, setIsLoading] = useState(true); 
  const [filterOptions, setFilterOptions] = useState();

  useEffect(() => {
    const genderFilter = filterOptions?.gender ?? '';
    const statusFilter = filterOptions?.status ?? '';
    const url = `https://rickandmortyapi.com/api/character/?gender=${genderFilter}&status=${statusFilter}`;

    const fetchItems = async () => {
      const result = await axios(url);

      setItems(result.data);
      setIsLoading(false);
    }

    fetchItems();
  }, [filterOptions])


  return (
    <div className="container">
      <Header></Header>
      <Filter setFilterOptions={setFilterOptions} filterOptions={filterOptions}></Filter>
      <CharacterGrid isLoading={isLoading} items={items}></CharacterGrid>
      <Pagination pages={5}></Pagination>
    </div>
  );
}

export default App;
