import React from 'react'

const Pagination = ({ setFilterOptions, filterOptions }) => {

    const handlePageChange = (e) => {
        setFilterOptions({
            ...filterOptions,
            page: 1
        });
    }

    const renderPages = () => {
        let result = '';
        for (let index = 1; index < 5; index++) {
            result += <li>{index}</li>
        }
    }

    return (
        <div className='pagination'>
            <ul>{renderPages}</ul>
        </div>
    )
}

export default Pagination
